function handleGroupMessage($message, $client, $user) {
    $keywords = getGroupTopics();
    $messageText = $message['text'];
    
    foreach ($keywords as $keyword) {
        if (mb_stripos($messageText, $keyword) !== false) {
            $text = "🧘 Хотите узнать об этом более подробнее, заходите в бот @helpme_yhc_bot - здесь есть все ответы.😉";
            
            sendMessage('sendMessage', [
                'chat_id' => $message['chat_id'],
                'text' => $text,
                'parse_mode' => 'HTML',
                'reply_to_message_id' => $message['message_id']
            ]);
            
            $text = '🧑‍💻 Нажмите 👉 /help 👈 и наш специалист ответит на все ваши вопросы максимально подробно.';
            sendMessage('sendMessage', [
                'chat_id' => $message['from_id'],
                'text' => $text,
                'parse_mode' => 'HTML'
            ]);
            
            return true;
        }
    }
    return false;
}

